
let menuParents = document.querySelectorAll('.content-pagemenu__block');
for (let index = 0; index < menuParents.length; index++) {
	const menuParent = menuParents[index];

	const devices = new RegExp('Android|webOS|iPhone|iPad|iPod|BlackBerry|BB|PlayBook|IEMobile|Windows Phone|Kindle|Silk|Opera Mini', "i");
		if (devices.test(navigator.userAgent))
		{
			menuParent.addEventListener("click", function (e) {
				menuParent.classList.toggle('_active');
			});
		}
		else
		{
			menuParent.addEventListener("mouseenter", function (e) {
				menuParent.classList.add('_active');
			});
			menuParent.addEventListener("mouseleave", function (e) {
				menuParent.classList.remove('_active');
			});
		}
}

let social_burger = document.querySelector('.icon-menu-page');
social_burger.addEventListener("click", function (e) {
	let social_burger_icon = document.querySelector('.icon-menu-page');
	let social_menu = document.querySelector('.content-pagemenu__menu');

	social_burger_icon.classList.toggle('_active');
	social_menu.classList.toggle('_active');
});

const burger=document.querySelector(".icon-menu");
burger.addEventListener("click",()=>{
    let burger_icon = document.querySelector('.icon-menu');
	let menu = document.querySelector('.menu-header__body');

	burger_icon.classList.toggle('_active');
	menu.classList.toggle('_active');
});
